con:config.script:
import java.io.*
import com.eviware.soapui.support.XmlHolder
import com.eviware.soapui.impl.wsdl.testcase.WsdlTestRunContext

/*groovyutils = new com.eviware.soapui.support.GroovyUtils(context)
projectpath = groovyutils.projectPath
log.info projectpath;

*/




// Load the Parameter values from properties file specified in PROPERTY_FILE_NAME value to test case
def props = new Properties()

new File(context.expand('${#TestCase#PROPERTY_FILE_NAME}')).withInputStream { 
stream -> properties.load(stream)
}
for(item in props)
{
	log.info item.key
	log.info item.value

testRunner.testCase.setPropertyValue(item.key,item.value)
}	

// defining all the vaiables

def name= testRunner.testCase.getPropertyValue("name")
def id= testRunner.testCase.getPropertyValue("id")
def department= testRunner.testCase.getPropertyValue("Department")
def age= testRunner.testCase.getPropertyValue("age")

//get the XML request for Add employee


def addReq= testRunner.testCase.testSteps["Add"].getPropertyValue("Request")
def xmlAdd=new XmlHolder(addReq) //created an object to place XML

//adding values to temp XML

xmlAdd.setNodeValue("//typ:addEmployee/typ:name",name)
xmlAdd.setNodeValue("//typ:addEmployee/typ:id",id)
xmlAdd.setNodeValue("//typ:addEmployee/typ:age",age)
xmlAdd.setNodeValue("//typ:addEmployee/typ:Department",department)

//saving in new XML object
def newAddXml= xmlAdd.getXml()

//setting and saving the values to Add xml using set Property method

testRunner.testCase.testSteps["Add"].setPropertyValue("Request",newAddXml)

//call the serice i:e rum the XML Add request

def control=testRunner.testCase.testSteps["Add"]
def contextAddEmployee= new WsdlTestRunContext(control)
control.run(testRunner,contextAddEmployee)

log.info "Employee added"

//Get the employee deatils and validate the response

def getReq= testRunner.testCase.testSteps["GetDetails"].getPropertyValue("Request")

def xmlGet=new XmlHolder(getReq)
xmlGet.setNodeValue("//typ:getEmployeeDetails/typ:employeeName",name)

def newGetXml= xmlGet.getXml()
testRunner.testCase.testSteps["GetDetails"].setPropertyValue("Request",newGetXml)

def control_1=testRunner.testCase.testSteps["GetDetails"]
def contextGetEmployee= new WsdlTestRunContext(control_1)
control_1.run(testRunner,contextGetEmployee)

log.info "Employee present in database"

//validate the response


def getResponse= testRunner.testCase.testSteps["GetDetails"].getPropertyValue("Response")
def xmlGetResponse=new XmlHolder(getResponse)
def temp= xmlGetResponse.getNodeValue("//ns:getEmployeeDetailsResponse/ns:return/ns:name")
assert temp== name
log.info "Employee addition validated"

//Delete Employee added


def deleteReq= testRunner.testCase.testSteps["Delete"].getPropertyValue("Request")
def xmlDelete=new XmlHolder(deleteReq)
xmlDelete.setNodeValue("//typ:deleteEmployee/typ:employeeName",name)
def newDeleteXml= xmlDelete.getXml()
testRunner.testCase.testSteps["Delete"].setPropertyValue("Request",newDeleteXml)
def control_2=testRunner.testCase.testSteps["Delete"]
def contextDeleteEmployee= new WsdlTestRunContext(control_2)
control_2.run(testRunner,contextDeleteEmployee)

log.info "Employee deleted"
